'use strict'

function createSiteFile (path, contents, mediaType) {
  return {
    contents: Buffer.from(contents),
    mediaType,
    out: { path },
    path: path,
    pub: { url: '/' + path, rootPath: '' },
    src: { stem: path.slice(0, path.lastIndexOf('.')) },
  }
}

module.exports = createSiteFile
